import React, {useState} from 'react';
import './Slider.scss';

export default class Slider extends React.Component {

  contructor(props) {
    this.sliderArr = [1, 2, 3, 4, 5];
    [x,setX] = useState(0);
    this.x = x;
    this.setX = setX;
    this.goLeft = this.goLeft.bind(this);
    this.goRight = this.goRight.bind(this);
  }

  goLeft = () => {
    x === 0 ? setX(-100 * (sliderArr.length - 1)) : setX(x + 100);
  }
  goRight = () => {
    x === -100 * (sliderArr.length - 1) ? setX(0) : setX(x - 100);
  }

  render () {

    let sliderArr = this.sliderArr;

    return (
        <div className="Slider">
          <button className="ArrowBtn" onClick={this.goLeft}><i className="arrow left"></i></button>
          <div className="Content">
            {
              sliderArr.map((item, index) => {
                return(
                  <div key={index} className= "Slide" style ={{transform:`translateX(${(100*index) + this.x}%) translateY(${(-100*index)}%)`}}>
                    <div className="Panel">
                      <img id="Pic" src={process.env.PUBLIC_URL + '/gamePreview.jpeg'} alt="Game1"></img>
                      <div className="TextBox">
                        <h2>The Court of the Crimson King</h2>
                        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore
                        et dolore magna aliqua. Ut enim ad minim veniam,
                        quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.
                        Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.
                        Excepteur sint occaecat cupidatat non proident, sunt.</p>
                        <button>more</button>
                      </div>
                    </div>
                  </div>
                )
              })
            }
          </div>
          <button className="ArrowBtn" onClick={this.goRight}><i className="arrow right"></i></button>
        </div>
      );
    }
}

