import React, { useState }  from 'react';
import {projectPromise, personalPromise} from './DataHolder.js'
import './TopBar.scss';

function TopBar(props){
  let prom1 = new Promise(projectPromise);
  let [sliderArr,setSlideArr] = useState([]);
  let prom2 = new Promise(personalPromise)
  let [personalObj,setPersonalObj] = useState(null);
  let shouldFocus = false;

  function clickProjTab(){
    let elem = document.getElementById("dropContent");
    elem.style.display = "block";
  }

  function loseProjTab(){
    if (shouldFocus === false)
    {
      let elem = document.getElementById("dropContent");
      elem.style.display = "none";
    }
  }

  function useFocus(){
    shouldFocus = true;
  }

  function removeFocus(){
    shouldFocus = false;
  }

  if (personalObj == null)
  {
    prom2.then((success, failed) => {
      setPersonalObj(success);
    })
  }

  if (sliderArr < 1)
  {
    prom1.then((success, failed) => {
      setSlideArr(success);
    })
  }

  return (
    <div id="navigation-bar">
      <nav>
        <ul>
          <li><a href="#Home" onClick={() => props.handler(0, personalObj)}>Home</a></li>
          <div className="dropdown">
            <button className="dropbtn" onClick={clickProjTab}>Projects <span id="Filler">_</span> <i className="arrow down"></i></button>
            <div className="dropdown-content" onFocus={clickProjTab} onBlur={loseProjTab} id="dropContent">
              <a href="#Projects" className="dropItem" onFocus={clickProjTab} onClick={() => props.handler(1, sliderArr[0])}>The Court of the Crimson King</a>
              <a href="#Projects" className="dropItem" onFocus={clickProjTab} onClick={() => props.handler(1, sliderArr[1])}>Canvas Dash</a>
              <a href="#Projects" className="dropItem" onFocus={clickProjTab} onClick={() => props.handler(1, sliderArr[2])}>Leinecker's Leins</a>
            </div>
          </div>
          <li><a href="#Personal" onClick={() => props.handler(2, personalObj)}>Personal</a></li>
        </ul>
      </nav>
    </div>
  );
}

export default TopBar;
