import React from 'react';
import SliderFull from './SliderFull.js';
import './HomePage.scss';


//import profPic from '.././public/michaelProf.jpeg'

export default class HomePage extends React.Component{

  componentDidMount() {

    this.GetAffirmation();

    // Adds the elements to the used tech list
    let listElem = document.getElementById("KnownList");

    // Clears the initial elems
    listElem.innerHTML = "";
    for (let i = 0; i < this.props.pictureObj.techKnown.length; i++)
    {
      listElem.innerHTML += (`<li>${this.props.pictureObj.techKnown[i]}</li>`);
    }

    // Adds the elements to the used tech list
    let listElem2 = document.getElementById("LearningList");

    // Clears the initial elems
    listElem2.innerHTML = "";
    for (let i = 0; i < this.props.pictureObj.techLearning.length; i++)
    {
      listElem2.innerHTML += (`<li>${this.props.pictureObj.techLearning[i]}</li>`);
    }

  }

  constructor(props) {

    super(props);
    this.state = {AffirmationNum: 0};
    // This binding is necessary to make `this` work in the callback
    this.GetAffirmation = this.GetAffirmation.bind(this);

  }

  GetAffirmation() {

    let RUNAPI = true;

    if (RUNAPI && this.state.AffirmationNum === 0) {

      const proxyurl = "https://cors-anywhere.herokuapp.com/";
      const url = proxyurl + 'https://www.affirmations.dev/';

      fetch(url)
      .then(val => val.text())
      .then((data) => {
        let obj = JSON.parse(data);
        console.log("Affirmation: " + obj.affirmation);
        document.getElementById("Affirmation").innerHTML = obj.affirmation;
      })
      .catch(reason => {
        console.error(reason);
      });

      this.setState({AffirmationNum: 1});

    }
    else
    {
      document.getElementById("Affirmation").innerHTML = "This is a test";
    }

  }

  render() {
    return (
      <div id="PageHolder">
        <div id="PictureSection">
            <img id="Pic" src={process.env.PUBLIC_URL + '/michaelProf.jpeg'} alt="Michael Ferrara"></img>
            <div className="PicTextHolder">
              <h1>Michael Ferrara: Developer</h1>
              <br/>
              <h3 id="Affirmation">Affirmation</h3>
            </div>
        </div>
        <div className="LineSpacer"></div>

        <h1 className='DarkFont'>Projects</h1>
        <SliderFull  handler = {this.props.handler.bind(this)}/>
        <div className="LineSpacer"></div>

        <h1 className='DarkFont'>Experience</h1>
        <div className="ListHolder">
          <div className="ListElement">
            <h2>Know</h2>
            <ul className="double" id="KnownList"></ul>
          </div>
          <div className="VertSpacer"></div>
          <div className="ListElement">
            <h2>Learning</h2>
            <ul className="double" id="LearningList"></ul>
          </div>
        </div>

      </div>
    );
  }
}

//export default HomePage;
