import React, { useState } from 'react';
import projectPromise from './DataHolder.js'
import './Slider.scss';

const Slider = () => {
  let prom = projectPromise();
  let sliderArr = getProjects();
  const [x,setX] = useState(0);
  const [keyNum,setKeyNum] = useState(0);

  const goLeft = () => {
    x === 0 ? setX(-100 * (sliderArr.length - 1)) : setX(x + 100);
  }
  const goRight = () => {

    x === -100 * (sliderArr.length - 1) ? setX(0) : setX(x - 100);
  }


  return (
      <div className="Slider">
        <button className="ArrowBtn" onClick={goLeft}><i className="arrow left"></i></button>
        <div key={{keyNum}} className="Content">
        {
          keyNum > 0 ? (
            <div>
              <p style={{color: 'red'}}>{sliderArr[0].name}</p>
            </div>
