//import React from 'react';
import { csv } from 'd3';

const slideData = {
  name: "",
  description: "",
  shortDescription: "",
  myRole: "",
  pictures: [],
  techUsed: []
};

const getProjects = (resolve, reject) => {

  let valArr = [];

  const row = (col) => {
    let data = JSON.parse(JSON.stringify(slideData));

    data.name = col.projectName;
    data.description = col.description;
    data.myRole = col.myRole;

    let picStr = col.pictures
    let techStr = col.techUsed;
    let longDesc = col.description;
    let shortDesc = "";
    let maxLen = 200;

    // Converts description to shortened version ending in ...
    for (let i = 0; i < longDesc.length; i++)
    {
      if (i >= maxLen)
      {
        if (/\s/.test(longDesc.charAt(i)))
        {
          shortDesc += "...";
          //console.log(shortDesc);
          break;
        }
        else
        {
          shortDesc += longDesc.charAt(i);
        }
      }
      else
      {
        shortDesc += longDesc.charAt(i);
      }
    }

    // Converts picture names from string to array
    let picArr = picStr.split(',');
    for (let i = 0; i < picArr.length; i++)
    {
      data.pictures.push(picArr[i]);
    }

    // Converts tech from string to array
    let techArr = techStr.split(',');
    for (let i = 0; i < techArr.length; i++)
    {
      data.techUsed.push(techArr[i]);
    }

    data.shortDescription = shortDesc;

    //console.log(data);

    valArr.push(data);

    //setKeyNum(valArr.length);
  }

  csv('data.csv', row).then( data => {
    //console.log(valArr);
    resolve(valArr);
    return;
  });
}

export default getProjects;
