import React, { useState } from 'react';
import {projectPromise, getProjects} from './DataHolder.js'
import './Slider.scss';


const Slider = (props) => {
  let prom = new Promise(projectPromise);
  let [sliderArr,setSlideArr] = useState([]);
  const [x,setX] = useState(0);
  const [keyNum,setKeyNum] = useState(0);

  function testFunc (index)
  {
    props.handler(1, sliderArr[index]);
    alert(index);
  }

  const goLeft = () => {
    let val = x;
    //console.log("from: " + -val/100);

    let elem1 = document.getElementById("more"+(-val/100));
    elem1.style.display = "none";

    val === 0 ? val = -100 * (sliderArr.length - 1) : val = x + 100;

    let elem2 = document.getElementById("more"+(-val/100));
    elem2.style.display = "block";

    //console.log("to: " + -val/100);
    setX(val);
  }

  const goRight = () => {
    let val = x;
    //console.log("from: " + -val/100);
    let elem1 = document.getElementById("more"+(-val/100));
    elem1.style.display = "none";

    x === -100 * (sliderArr.length - 1) ? val = 0 : val = x - 100;

    let elem2 = document.getElementById("more"+(-val/100));
    elem2.style.display = "block";

    //console.log("to: " + -val/100);
    setX(val);
  }

  if (sliderArr < 1)
  {
    prom.then((success, failed) => {
      setSlideArr(success);
      setKeyNum(1);
    })
  }

  return (
      <div className="Slider">
        <button className="ArrowBtn" onClick={goLeft}><i className="arrow left"></i></button>
        <div key={{keyNum}} className="Content">
        {
          keyNum > 0 ? (
            sliderArr.map((item, index) => {
              return(
                <div key= {index} className= "Slide" style ={{transform:`translateX(${(100*index) + x}%) translateY(${(-100*index)}%)`}}>
                  <div className="Panel">
                    <img className="Pic" src={process.env.PUBLIC_URL + item.pictures[0]} alt="Game1"></img>
                    <div className="TextBox">
                      <h2>{item.name}</h2>
                      <p>{item.shortDescription}</p>
                      <button id={"more" + index} onClick={() => {testFunc(index)}}>more</button>
                    </div>
                  </div>
                </div>
              )
            })
          ) : (
            <div>
              <p style={{color: 'black'}}>testing</p>
            </div>
          )
        }
        </div>
        <button className="ArrowBtn" onClick={goRight}><i className="arrow right"></i></button>
      </div>
    );

}

export default Slider;
