      </div>
    );
  }
  else if(projState === 1)
  {
    return
    (
      <div className="Page">
        <TopBar handler = {handler.bind(this)}/>
        <ProjectsPage projectObj = {projectObj}/>
      </div>
    );
  }
  else if(projState === 2)
  {
    return
    (
      <div className="Page">
        <TopBar handler = {handler.bind(this)}/>
        <PersonalPage pictureObj = {pictureObj}/>
      </div>
    );
  }
  */

}

export default MainPage;
