import React from 'react';
import HomePage from './HomePage.js';
import ProjectsPage from './ProjectsPage.js';
import TopBar from './TopBar.js';

export class MainPage extends React.Component{

  constructor(props) {
    super(props);
    // 0-home, 1-projects, 2-personal
    this.state = {projState: 0};

    // This binding is necessary to make `this` work in the callback
    this.handler = this.handler.bind(this);
  }

  handler (num) {
    this.state.projState = num;
    console.log(this.state.projState);

    this.forceUpdate();
  }

  render() {
    console.log("RUN: " + this.state.projState);
    let handler = this.handler;

    if (this.state.projState === 0) {
      return (
        <div className="Page">
          <TopBar handler = {handler.bind(this)}/>
          <HomePage/>
        </div>
      );
    } else if(this.state.projState === 1) {
      return (
        <div className="Page">
          <TopBar handler = {handler.bind(this)}/>
          <ProjectsPage/>
        </div>
      );
    } else if(this.state.projState === 2) {
      return (
        <div className="Page">
          <TopBar handler = {handler.bind(this)}/>
          <ProjectsPage/>
        </div>
      );
    }

  }

  GetPath () {
    console.log("Path: " + this.props.location);
  }
}
