import React, { useState }  from 'react';
import {projectPromise} from './DataHolder.js'
import './TopBar.scss';

function TopBar(props){
  let prom = new Promise(projectPromise);
  let [sliderArr,setSlideArr] = useState([]);
  const [x,setX] = useState(0);
  const [keyNum,setKeyNum] = useState(0);

  if (sliderArr < 1)
  {
    prom.then((success, failed) => {
      setSlideArr(success);
    })
  }

  return (
    <div id="navigation-bar">
      <nav>
        <ul>
          <li><a href="#Home" onClick={() => props.handler(0)}>Home</a></li>
          <div className="dropdown">
            <button className="dropbtn">Projects <span id="Filler">_</span> <i className="arrow down"></i>
            </button>
            <div className="dropdown-content">
              <a href="#Projects" onClick={() => props.handler(1, sliderArr[0])}>The Court of the Crimson King</a>
              <a href="#Projects" onClick={() => props.handler(1, sliderArr[1])}>Canvas Dash</a>
              <a href="#Projects" onClick={() => props.handler(1, sliderArr[2])}>Leinecker's Leins</a>
            </div>
          </div>
          <li><a href="#Personal" onClick={() => props.handler(2)}>Personal</a></li>
        </ul>
      </nav>
    </div>
  );
}

export default TopBar;
