import React, { useState } from 'react';
import './SliderImg.scss';


const SliderImg = (props) => {
  let sliderArr = props.projectObj.pictures;
  const [x,setX] = useState(0);
  const [keyNum,setKeyNum] = useState(0);

  const goLeft = () => {
    let val = x;

    val === 0 ? val = -100 * (sliderArr.length - 1) : val = x + 100;

    setX(val);
  }

  const goRight = () => {
    let val = x;

    x === -100 * (sliderArr.length - 1) ? val = 0 : val = x - 100;

    setX(val);
  }

  return (
    <div id="ProjCarousel">
      <div id="ProjPicHolder">
        <button className="ArrowBtn" onClick={goLeft}><i className="arrow left"></i></button>
        <div key={{keyNum}} className="Content">
          {
            sliderArr.map((item, index) => {
              return(
                <img id="ProjPic" src={process.env.PUBLIC_URL + '/' + props.projectObj.pictures[0]} alt="Game1"></img>
              )
            })
          }
        </div>
        <button className="ArrowBtn" onClick={goRight}><i className="arrow right"></i></button>
      </div>
    </div>
  );

}

export default SliderImg;

