import React from 'react';
import './HomePage.scss';
const https = require('https');


//import profPic from '.././public/michaelProf.jpeg'

export class HomePage extends React.Component{

  constructor(props) {

    super(props);
    this.state = {AffirmationNum: 0};

    // This binding is necessary to make `this` work in the callback
    this.GetAffirmation = this.GetAffirmation.bind(this);

    this.GetAffirmation();
  }

  GetAffirmation() {

    if (this.state.AffirmationNum === 0) {

      const proxyurl = "https://cors-anywhere.herokuapp.com/";
      const url = proxyurl + 'https://www.affirmations.dev/';

      fetch(url)
      .then(val => val.text())
      .then((data) => {
        let obj = JSON.parse(data);
        console.log("Affirmation: " + obj.affirmation);
        document.getElementById("Affirmation").innerHTML = obj.affirmation;
      })
      .catch(reason => {
        console.error(reason);
      });

      this.state.AffirmationNum = 1;
    }

  }

  render() {
    return (
      <div id="PageHolder">
        <div id="PictureSection">
            <img id="Pic" src={process.env.PUBLIC_URL + '/michaelProf.jpeg'} alt="Michael Ferrara"></img>
            <div className="PicTextHolder">
              <h1>Michael Ferrara: Developer</h1>
              <br/>
              <h3 id="Affirmation"></h3>
            </div>
        </div>
        <div className="LineSpacer"></div>

        <h1 className='DarkFont'>Projects</h1>
        <div id="Carousel">
        <button className="ArrowBtn"><i className="arrow left"></i></button>
          <div id="ProjectsCarousel">
            <img id="Pic" src={process.env.PUBLIC_URL + '/gamePreview.jpeg'} alt="Game1"></img>
            <div id="TextBox">
              <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore
              et dolore magna aliqua. Ut enim ad minim veniam,
              quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.
              Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.
              Excepteur sint occaecat cupidatat non proident, sunt.</p>
              <button>more</button>
            </div>
          </div>
          <button className="ArrowBtn"><i className="arrow right"></i></button>
        </div>
        <div className="LineSpacer"></div>

        <h1 className='DarkFont'>Experience</h1>
        <div className="ListHolder">
          <div className="ListElement">
            <h2>Know</h2>
            <ul className="double">
              <li>Java</li>
              <li>JavaScript</li>
              <li>Unity</li>
              <li>C#</li>
              <li>C</li>
              <li>HTML/CSS</li>
              <li>Vue.js</li>
              <li>Photoshop</li>
              <li>Illustrator</li>
            </ul>
          </div>
          <div className="VertSpacer"></div>
          <div className="ListElement">
            <h2>Learning</h2>
            <ul className="double">
              <li>Python</li>
              <li>C++</li>
              <li>PHP</li>
              <li>Ruby</li>
            </ul>
          </div>
        </div>

      </div>
    );
  }
}

//export default HomePage;
