import React, {useState} from 'react';
import HomePage from './HomePage.js';
import ProjectsPage from './ProjectsPage.js';
import PersonalPage from './PersonalPage.js';
import TopBar from './TopBar.js';

import {personalPromise} from './DataHolder.js'

export class MainPage extends React.Component{

  constructor(props) {
    window.history.replaceState({}, document.title, "/#Home");
    super(props);
    // 0-home, 1-projects, 2-personal
    this.state = {projState: 0,
                  projectObj: null,
                  pictureObj: null};

    // This binding is necessary to make `this` work in the callback
    this.handler = this.handler.bind(this);
    let prom2 = new Promise(personalPromise)
    let [personalObj,setPersonalObj] = useState(null);

    if (personalObj == null)
    {
      prom2.then((success, failed) => {
        setPersonalObj(success);
      })
    }
  }

  handler (num, Obj) {
    window.scrollTo(0, 0);
    if (this.state.projState !== num || (this.state.projState === 1 && this.state.projectObj !== Obj))
    {

      if (num === 1 && Obj)
      {
        this.setState({
          projState: num,
          projectObj: Obj
        })
      }
      else if (num === 2 && Obj)
      {
        this.setState({
          projState: num,
          pictureObj: Obj
        })
      }
      else if (num === 0 && Obj)
      {
        this.setState({
          projState: num,
          pictureObj: Obj
        });
      }

      this.forceUpdate();
      console.log("Page: " + num);
    }
  }

  render() {
    let handler = this.handler;

    console.log("switching: " + this.state.projState);

    if (this.state.projState === 0) {
      return (
        <div className="Page">
          <TopBar handler = {handler.bind(this)}/>
          <HomePage handler = {handler.bind(this)} pictureObj = {this.state.pictureObj}/>
        </div>
      );
    } else if(this.state.projState === 1) {
      return (
        <div className="Page">
          <TopBar handler = {handler.bind(this)}/>
          <ProjectsPage projectObj = {this.state.projectObj}/>
        </div>
      );
    } else if(this.state.projState === 2) {
      return (
        <div className="Page">
          <TopBar handler = {handler.bind(this)}/>
          <PersonalPage pictureObj = {this.state.pictureObj}/>
        </div>
      );
    }

  }
}
