import React, { useState, useEffect } from 'react';
import { csv } from 'd3';
import './Slider.scss';

const slideData = {
  name: "",
  description: "",
  shortDescription: "",
  myRole: "",
  pictures: [],
  Tech: []
};

const Slider = () => {

  const sliderArr = [1, 2, 3, 4, 5];
  const [x,setX] = useState(0);
  /*
  this.goLeft = this.goLeft.bind(this);
  this.goRight = this.goRight.bind(this);
*/
/*
  state = {
    x: 0,
    setX: (num) =>{this.setState(
        prevState => ({x: num})
      )},
    sliderArr: [1, 2, 3, 4, 5]
  };

  contructor(props) {
    this.sliderArr = [1, 2, 3, 4, 5];
    const [x,setX] = useState(0);
    this.goLeft = this.goLeft.bind(this);
    this.goRight = this.goRight.bind(this);


  }

  componentDidMount () {

    useEffect(() => {
      csv('data.csv').then( data => {
        alert(data);
      });
    }, []);
  }
*/
  const goLeft = () => {

    x === 0 ? setX(-100 * (sliderArr.length - 1)) : setX(x + 100);
  }
  const goRight = () => {

    x === -100 * (sliderArr.length - 1) ? setX(0) : setX(x - 100);
  }

  const row = (col) => {
    let data = JSON.parse(JSON.stringify(slideData));

    data.name = col.projectName;
    data.description = col.description;
    let longDesc = col.description;
    let maxLen = 150;

    for (let i = 0; i < longDesc.length; i++)
    {
      if (i > maxLen)
      {
        alert("Hit");
        if (/\s/.test(longDesc.charAt(i)))
        {
          alert("Whitespace");
        }
      }
    }

  }

  useEffect(() => {
    csv('data.csv', row).then( data => {
      console.log(data);
    });
  }, []);

  return (
      <div className="Slider">
        <button className="ArrowBtn" onClick={goLeft}><i className="arrow left"></i></button>
        <div className="Content">
          {
            sliderArr.map((item, index) => {
              return(
                <div key={index} className= "Slide" style ={{transform:`translateX(${(100*index) + x}%) translateY(${(-100*index)}%)`}}>
                  <div className="Panel">
                    <img id="Pic" src={process.env.PUBLIC_URL + '/gamePreview.jpeg'} alt="Game1"></img>
                    <div className="TextBox">
                      <h2>The Court of the Crimson King</h2>
                      <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore
                      et dolore magna aliqua. Ut enim ad minim veniam,
                      quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.
                      Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.
                      Excepteur sint occaecat cupidatat non proident, sunt.</p>
                      <button>more</button>
                    </div>
                  </div>
                </div>
              )
            })
          }
        </div>
        <button className="ArrowBtn" onClick={goRight}><i className="arrow right"></i></button>
      </div>
    );

}

export default Slider;

